import React, { useState } from 'react';
import './styles/tailwind.css';
import Dashboard from './pages/Dashboard';
import BookingForms from './pages/BookingForms';
import Appointments from './pages/Appointments';
import Settings from './pages/Settings';
import LoadingOverlay from './components/LoadingOverlay';

function App() {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [isLoading, setIsLoading] = useState(false);

  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', component: Dashboard },
    { id: 'booking-forms', label: 'Booking Forms', component: BookingForms },
    { id: 'appointments', label: 'Appointments', component: Appointments },
    { id: 'settings', label: 'Settings', component: Settings },
  ];

  const handleNavigation = (pageId) => {
    setCurrentPage(pageId);
  };

  const CurrentComponent =
    menuItems.find((item) => item.id === currentPage)?.component || Dashboard;

  return (
    <div
      id="wd-appointments-admin-app"
      className="wda-min-h-screen wda-bg-gray-50"
    >
      <div className="wda-flex">
        {/* Sidebar Navigation */}
        <nav className="wda-w-60 wda-fixed wda-h-full wda-bg-white wda-border-r wda-border-gray-200">
          <ul className="wda-py-4">
            {menuItems.map((item) => (
              <li key={item.id} className="wda-px-2">
                <button
                  onClick={() => handleNavigation(item.id)}
                  className={`wda-w-full wda-text-left wda-px-4 wda-py-2 wda-rounded-lg wda-transition-colors
                    ${
                      currentPage === item.id
                        ? 'wda-bg-primary-50 wda-text-primary-700'
                        : 'wda-text-gray-700 hover:wda-bg-gray-50'
                    }`}
                >
                  {item.label}
                </button>
              </li>
            ))}
          </ul>
        </nav>

        {/* Main Content */}
        <main className="wda-flex-1 wda-ml-60 wda-p-8">
          <div className="wda-max-w-7xl wda-mx-auto">
            <CurrentComponent setIsLoading={setIsLoading} />
          </div>
        </main>
      </div>

      {/* Loading Overlay */}
      {isLoading && <LoadingOverlay />}
    </div>
  );
}

export default App;
