import React from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import {
  Box,
  Drawer,
  AppBar,
  Toolbar,
  Typography,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  IconButton,
  useMediaQuery,
} from '@mui/material';
import {
  Dashboard as DashboardIcon,
  EventNote as EventNoteIcon,
  Settings as SettingsIcon,
  Description as DescriptionIcon,
  Menu as MenuIcon,
} from '@mui/icons-material';

// Import contexts and providers
import { AppProvider } from './context/AppContext';
import { SettingsProvider, useSettings } from './context/SettingsContext';
import { CalendarProvider } from './context/CalendarContext';

// Import components
import LoadingOverlay from './components/LoadingOverlay';
import Notifications from './components/Notifications';

// Import pages
import Dashboard from './pages/Dashboard';
import BookingForms from './pages/BookingForms';
import Appointments from './pages/Appointments';
import Settings from './pages/Settings';

// Create default theme
const defaultTheme = createTheme({
  palette: {
    primary: {
      main: '#2271b1', // WordPress admin blue
    },
    secondary: {
      main: '#6c757d',
    },
  },
  typography: {
    fontFamily:
      '-apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Helvetica Neue",sans-serif',
  },
  shape: {
    borderRadius: 4,
  },
  components: {
    MuiAppBar: {
      styleOverrides: {
        root: {
          boxShadow: 'none',
          backgroundColor: 'transparent',
          color: '#1d2327',
          position: 'relative',
        },
      },
    },
    MuiDrawer: {
      styleOverrides: {
        root: {
          position: 'relative',
          width: '100%',
        },
        paper: {
          position: 'relative',
          border: 'none',
          background: 'transparent',
          width: '100%',
        },
      },
    },
    MuiToolbar: {
      styleOverrides: {
        root: {
          minHeight: '48px',
        },
      },
    },
  },
});

function MainApp({ initialPage }) {
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const navigate = useNavigate();
  const { settings } = useSettings();
  const isMobile = useMediaQuery(defaultTheme.breakpoints.down('sm'));

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const menuItems = [
    {
      text: 'Dashboard',
      icon: <DashboardIcon />,
      path: 'admin.php?page=wd-appointments',
    },
    {
      text: 'Booking Forms',
      icon: <DescriptionIcon />,
      path: 'admin.php?page=wd-appointments-forms',
    },
    {
      text: 'Appointments',
      icon: <EventNoteIcon />,
      path: 'admin.php?page=wd-appointments-list',
    },
    {
      text: 'Settings',
      icon: <SettingsIcon />,
      path: 'admin.php?page=wd-appointments-settings',
    },
  ];

  const handleNavigation = (path) => {
    // Get WordPress admin URL from the global object
    const wpAdminUrl = window.wdAppointments?.wpAdminUrl || '/wp-admin/';
    window.location.href = `${wpAdminUrl}${path}`;
  };

  const drawer = (
    <List>
      {menuItems.map((item) => (
        <ListItem
          button
          key={item.text}
          onClick={() => {
            handleNavigation(item.path);
            if (isMobile) {
              setMobileOpen(false);
            }
          }}
        >
          <ListItemIcon>{item.icon}</ListItemIcon>
          <ListItemText primary={item.text} />
        </ListItem>
      ))}
    </List>
  );

  React.useEffect(() => {
    // Map WordPress page parameters to React routes
    const pathMap = {
      'wd-appointments': '/dashboard',
      'wd-appointments-forms': '/forms',
      'wd-appointments-list': '/appointments',
      'wd-appointments-settings': '/settings',
    };

    // Get the current page from URL parameters
    const urlParams = new URLSearchParams(window.location.search);
    const currentPage = urlParams.get('page');

    // Navigate to the corresponding React route
    navigate(pathMap[currentPage] || '/dashboard');
  }, [initialPage, navigate]);

  // Create theme based on settings
  const theme = React.useMemo(
    () =>
      createTheme({
        ...defaultTheme,
        palette: {
          ...defaultTheme.palette,
          primary: {
            main:
              settings?.appearance?.primaryColor ||
              defaultTheme.palette.primary.main,
          },
          secondary: {
            main:
              settings?.appearance?.secondaryColor ||
              defaultTheme.palette.secondary.main,
          },
        },
        typography: {
          ...defaultTheme.typography,
          fontFamily:
            settings?.appearance?.fontFamily ||
            defaultTheme.typography.fontFamily,
        },
        shape: {
          ...defaultTheme.shape,
          borderRadius:
            settings?.appearance?.borderRadius ||
            defaultTheme.shape.borderRadius,
        },
      }),
    [settings?.appearance]
  );

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex', flexDirection: 'column', width: '100%' }}>
        {isMobile && (
          <Box sx={{ mb: 2 }}>
            <AppBar position="relative">
              <Toolbar>
                <IconButton
                  color="inherit"
                  aria-label="open drawer"
                  edge="start"
                  onClick={handleDrawerToggle}
                  sx={{ mr: 2 }}
                >
                  <MenuIcon />
                </IconButton>
                <Typography variant="h6" noWrap component="div">
                  WD Appointments
                </Typography>
              </Toolbar>
            </AppBar>
          </Box>
        )}

        <Box sx={{ display: 'flex', width: '100%' }}>
          <Box
            component="nav"
            sx={{
              width: { xs: '100%', sm: 240 },
              flexShrink: 0,
              display: { xs: mobileOpen ? 'block' : 'none', sm: 'block' },
            }}
          >
            {drawer}
          </Box>

          <Box
            component="main"
            sx={{
              flexGrow: 1,
              p: { xs: 1, sm: 3 },
              width: { xs: '100%', sm: `calc(100% - 240px)` },
            }}
          >
            <Routes>
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/forms" element={<BookingForms />} />
              <Route path="/appointments" element={<Appointments />} />
              <Route path="/settings" element={<Settings />} />
            </Routes>
          </Box>
        </Box>

        <Notifications />
        <LoadingOverlay />
      </Box>
    </ThemeProvider>
  );
}

// Wrap the main app with providers
function App({ initialPage }) {
  return (
    <AppProvider>
      <SettingsProvider>
        <CalendarProvider>
          <MainApp initialPage={initialPage} />
        </CalendarProvider>
      </SettingsProvider>
    </AppProvider>
  );
}

export default App;
