import React, { useState } from 'react';
import GeneralSettings from '../components/settings/GeneralSettings';
import EmailSettings from '../components/settings/EmailSettings';
import AppearanceSettings from '../components/settings/AppearanceSettings';
import CalendarIntegration from '../components/settings/CalendarIntegration';
import LicenseSettings from '../components/settings/LicenseSettings';
import AdvancedSettings from '../components/settings/AdvancedSettings';
import SystemInfo from '../components/settings/SystemInfo';
import { useApp } from '../context/AppContext';

function TabPanel({ children, value, index }) {
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`settings-tabpanel-${index}`}
      aria-labelledby={`settings-tab-${index}`}
      className="wda-py-3"
    >
      {value === index && children}
    </div>
  );
}

const Settings = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [saving, setSaving] = useState(false);
  const [settings, setSettings] = useState({
    general: {},
    email: {},
    appearance: {},
    calendar: {},
    license: {},
    advanced: {},
  });
  const { showNotification } = useApp();

  const handleTabChange = (index) => {
    setActiveTab(index);
  };

  const handleSettingsUpdate = (section, newSettings) => {
    setSettings((prev) => ({
      ...prev,
      [section]: newSettings,
    }));
  };

  const handleSaveSettings = async () => {
    try {
      setSaving(true);
      await new Promise((resolve) => setTimeout(resolve, 1000));
      showNotification('success', 'Settings saved successfully');
    } catch (error) {
      showNotification('error', 'Failed to save settings');
    } finally {
      setSaving(false);
    }
  };

  const tabs = [
    {
      label: 'General',
      component: (
        <GeneralSettings
          settings={settings.general}
          onUpdate={(newSettings) =>
            handleSettingsUpdate('general', newSettings)
          }
        />
      ),
    },
    {
      label: 'Email',
      component: (
        <EmailSettings
          settings={settings.email}
          onUpdate={(newSettings) => handleSettingsUpdate('email', newSettings)}
        />
      ),
    },
    {
      label: 'Appearance',
      component: (
        <AppearanceSettings
          settings={settings.appearance}
          onUpdate={(newSettings) =>
            handleSettingsUpdate('appearance', newSettings)
          }
        />
      ),
    },
    {
      label: 'Calendar',
      component: (
        <CalendarIntegration
          settings={settings.calendar}
          onUpdate={(newSettings) =>
            handleSettingsUpdate('calendar', newSettings)
          }
        />
      ),
    },
    {
      label: 'License',
      component: (
        <LicenseSettings
          settings={settings.license}
          onUpdate={(newSettings) =>
            handleSettingsUpdate('license', newSettings)
          }
        />
      ),
    },
    {
      label: 'Advanced',
      component: (
        <AdvancedSettings
          settings={settings.advanced}
          onUpdate={(newSettings) =>
            handleSettingsUpdate('advanced', newSettings)
          }
        />
      ),
    },
    {
      label: 'System Info',
      component: <SystemInfo />,
    },
  ];

  return (
    <div className="wda-w-full wda-relative">
      {/* Header */}
      <div className="wda-flex wda-justify-between wda-items-center wda-mb-6">
        <h1 className="wda-text-2xl wda-font-bold wda-text-gray-900">
          Settings
        </h1>
        <button
          onClick={handleSaveSettings}
          disabled={saving}
          className="wda-btn-primary"
        >
          {saving ? (
            <div className="wda-flex wda-items-center wda-space-x-2">
              <div className="wda-animate-spin wda-h-4 wda-w-4 wda-border-2 wda-border-white wda-border-t-transparent wda-rounded-full"></div>
              <span>Saving...</span>
            </div>
          ) : (
            'Save Settings'
          )}
        </button>
      </div>

      {/* Tabs */}
      <div className="wda-bg-white wda-rounded-lg wda-shadow-sm wda-mb-6">
        <div className="wda-border-b wda-border-gray-200">
          <nav
            className="wda-flex wda-space-x-4 wda-overflow-x-auto wda-p-4"
            role="tablist"
          >
            {tabs.map((tab, index) => (
              <button
                key={index}
                onClick={() => handleTabChange(index)}
                className={`wda-tab ${
                  activeTab === index
                    ? 'wda-bg-primary-50 wda-text-primary-700'
                    : 'wda-text-gray-500 hover:wda-text-gray-700 hover:wda-bg-gray-50'
                }`}
                role="tab"
                aria-selected={activeTab === index}
                aria-controls={`settings-tabpanel-${index}`}
                id={`settings-tab-${index}`}
              >
                {tab.label}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Tab Panels */}
      <div className="wda-relative wda-pb-20">
        {tabs.map((tab, index) => (
          <TabPanel key={index} value={activeTab} index={index}>
            {tab.component}
          </TabPanel>
        ))}

        {/* Sticky Save Settings Bar */}
        <div className="wda-fixed wda-bottom-0 wda-left-0 wda-right-0 wda-bg-white wda-border-t wda-border-gray-200 wda-p-4 wda-shadow-lg wda-z-10">
          <div className="wda-max-w-7xl wda-mx-auto wda-flex wda-justify-end">
            <button
              onClick={handleSaveSettings}
              disabled={saving}
              className="wda-btn-primary"
            >
              {saving ? (
                <div className="wda-flex wda-items-center wda-space-x-2">
                  <div className="wda-animate-spin wda-h-4 wda-w-4 wda-border-2 wda-border-white wda-border-t-transparent wda-rounded-full"></div>
                  <span>Saving...</span>
                </div>
              ) : (
                'Save Changes'
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
